tinymce.PluginManager.add('TxImage', function(editor, url) {
	// Add a button that opens a window
    editor.addButton('TxImage', {
        icon: "image",
		tooltip:"Insert image",
        onclick: function() {
            // Open window
            editor.windowManager.open({
                title: 'Insert image',
                url: _url('/code/js/TxImage/TxImage.asp'),
                width: 780,
                height: 500
            });
        }
    });
    
});
