tinymce.PluginManager.add('TxLink', function(editor, url) {
	// Add a button that opens a window
    editor.addButton('TxLink', {
        icon: "link",
		tooltip:"Insert link",
        onclick: function() {
            // Open window
            editor.windowManager.open({
                title: 'Insert link',
                url: _url('/code/js/TxLink/TxLink.asp'),
                width: 650,
                height: 370
            });
        }
    });    
});